import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const flaskURL = 'http://localhost:5001';

function HomePage() {
  const [images, setImages] = useState([]);
  const [file, setFile] = useState(null);

  const fetchImages = async () => {
    const res = await axios.get(`${flaskURL}/images`);
    setImages(res.data);
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('image', file);
    await axios.post(`${flaskURL}/upload`, formData);
    fetchImages();
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>GeoSnapShot Photo Editor</h1>
      <div style={{ textAlign: 'center' }}>
        <input type="file" onChange={e => setFile(e.target.files[0])} />
        <br />
        <button onClick={handleUpload}>Upload</button>
      </div>

      <div className="gallery" style={{ marginTop: '2rem' }}>
        {images.map(name => (
          <div className="gallery-item" key={name}>
            <img src={`${flaskURL}/uploads/${name}`} alt={name} />
            <br />
            <Link to={`/editor/${name}`}>
              <button style={{ marginTop: '0.5rem' }}>Edit</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
