import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const flaskURL = 'http://localhost:5001';

function HomePage() {
  const [images, setImages] = useState([]);
  const [file, setFile] = useState(null);

  const fetchImages = async () => {
    try {
      const res = await axios.get(`${flaskURL}/images`);
      setImages(res.data);
    } catch (err) {
      console.error("Failed to fetch images:", err);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('image', file);

    try {
      await axios.post(`${flaskURL}/upload`, formData);
      setFile(null);
      fetchImages();
    } catch (err) {
      console.error("Image upload failed:", err);
    }
  };

  return (
    <div className="container fade-in" style={{ textAlign: 'center', padding: '2rem' }}>
      <h1 className="title" style={{ marginBottom: '2rem' }}>üì∑ GeoSnapShot Photo Editor</h1>

      <div style={{ marginBottom: '1rem' }}>
        <label className="file-upload-label" style={{
          border: '2px dashed #aaa',
          padding: '1rem 2rem',
          borderRadius: '10px',
          cursor: 'pointer',
          display: 'inline-block'
        }}>
          üìÅ Choose Image
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            style={{ display: 'none' }}
          />
        </label>
      </div>

      <div style={{ marginBottom: '1rem' }}>
        <button
          onClick={handleUpload}
          className="upload-btn"
          style={{
            padding: '10px 20px',
            fontSize: '1rem',
            backgroundColor: '#3f51b5',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            marginRight: '1rem'
          }}
        >
          ‚¨ÜÔ∏è Upload Image
        </button>

        <Link to="/cutout">
          <button
            style={{
              padding: '10px 20px',
              fontSize: '1rem',
              backgroundColor: '#009688',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            ‚úÇÔ∏è Try Group Photo Cutout
          </button>
        </Link>
      </div>

      <div className="gallery-container" style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        gap: '1.5rem',
        marginTop: '2rem'
      }}>
        {images.map(name => (
          <div key={name} className="image-box" style={{
            border: '1px solid #ddd',
            padding: '1rem',
            borderRadius: '8px',
            textAlign: 'center'
          }}>
            <img
              src={`${flaskURL}/uploads/${name}`}
              alt={name}
              style={{ width: '200px', borderRadius: '6px' }}
            />
            <br />
            <Link to={`/editor/${name}`}>
              <button style={{
                marginTop: '0.5rem',
                padding: '6px 12px',
                fontSize: '0.9rem',
                backgroundColor: '#673ab7',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}>
                üé® Edit
              </button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
