import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import {
  Box,
  Button,
  Heading,
  Input,
  Image,
  SimpleGrid,
  VStack,
  Text
} from '@chakra-ui/react';

const flaskURL = 'http://localhost:5001';

function HomePage() {
  const [images, setImages] = useState([]);
  const [file, setFile] = useState(null);

  const fetchImages = async () => {
    const res = await axios.get(`${flaskURL}/images`);
    setImages(res.data);
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('image', file);
    await axios.post(`${flaskURL}/upload`, formData);
    fetchImages();
  };

  return (
    <Box p={8}>
      <VStack spacing={4}>
        <Heading size="lg" mb={4}>GeoSnapShot Photo Editor</Heading>
        <Input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <Button colorScheme="teal" onClick={handleUpload}>Upload</Button>
      </VStack>

      <SimpleGrid columns={[1, 2, 3]} spacing={6} mt={10}>
        {images.map((name) => (
          <Box
            key={name}
            borderWidth="1px"
            borderRadius="md"
            overflow="hidden"
            p={4}
            textAlign="center"
            boxShadow="md"
            bg="white"
            _hover={{ transform: "scale(1.02)", transition: "0.2s" }}
          >
            <Image
              src={`${flaskURL}/uploads/${name}`}
              alt={name}
              boxSize="150px"
              objectFit="cover"
              mx="auto"
            />
            <Text mt={2}>{name}</Text>
            <Link to={`/editor/${name}`}>
              <Button mt={3} colorScheme="blue" size="sm">
                Edit
              </Button>
            </Link>
          </Box>
        ))}
      </SimpleGrid>
    </Box>
  );
}

export default HomePage;
